func BufferRings(nrings int) map[int][][]int {
	o := make(map[int][][]int, nrings)
	for k := 1; k <= nrings; k++ {
		o[k] = make([][]int, 0, 4*2*k)
		for m := 1 - k; m <= k-1; m++ {
			o[k] = append(o[k], []int{m, -k})
			o[k] = append(o[k], []int{m, k})
			o[k] = append(o[k], []int{k, m})
			o[k] = append(o[k], []int{-k, m})
		}
		// corners
		o[k] = append(o[k], []int{-k, -k})
		o[k] = append(o[k], []int{k, -k})
		o[k] = append(o[k], []int{k, k})
		o[k] = append(o[k], []int{-k, k})
	}
	return o
}
